mapper.select:
select th.*,u.username from tenwords_home th
INNER JOIN `user` u on u.userid=th.userid
where 1=1

    and th.createtime <#{todayling}


    and th.createtime >=#{yesterDayDate}

    and th.hometype=#{hometype}
    and th.status >=#{status}

    LIMIT #{page},#{pageitem}

select ur.userid,r.rolename,r.rolestate,p.permissionid,p.permissionname,p.url,p.type from permission p INNER JOIN role_permission rp on rp.permission=p.permissionid inner join role r on r.roleid=rp.roleid INNER JOIN user_role ur on ur.roleid=r.roleid WHERE r.rolestate=#{rolestate} and p.type=#{type}
and ur.userid=#{userid}
GROUP BY permissionname ORDER BY p.id asc
